package com.project.sagar.loctrack;

import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import com.daimajia.slider.library.*;
import com.daimajia.slider.library.SliderTypes.*;
import com.project.sagar.loctrack.Common;

public class SliderActivity extends AppCompatActivity {

    com.daimajia.slider.library.SliderLayout sliderShow;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_slider);

        sliderShow = (SliderLayout) findViewById(R.id.slider);
        TextSliderView textSliderView = new TextSliderView(this);
        textSliderView
                .description("Game of Thrones")
                .image("http://"+Common.IP+"/startup/upload_images/profile_sagar.jpeg");
        TextSliderView textSliderView2 = new TextSliderView(this);
        textSliderView2
                .description("cool ")
                .image("data:image/jpeg;base64,");

        sliderShow.addSlider(textSliderView);
        sliderShow.addSlider(textSliderView2);
    }

    @Override
    protected void onStop() {
        sliderShow.stopAutoCycle();
        super.onStop();
    }
}
